          amount: formData.price,
          currency: "inr",
          metadata: {
            consultationId: data.id,
            type: "consultation",
          },
        });
      }
    },
    onError: (error: any) => {
      toast({
        title: "Booking Failed",
        description: error.message || "Failed to book consultation",
        variant: "destructive",
      });
