import { useState } from "react";
import { Link, useLocation } from "wouter";
import { useMutation } from "@tanstack/react-query";
import { GlassCard } from "@/components/ui/glass-card";
import { NeonButton } from "@/components/ui/neon-button";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Checkbox } from "@/components/ui/checkbox";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Eye, EyeOff, User, Lock, Mail, Phone, Smartphone, Shield, AlertCircle } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
// AutoTranslate import removed for fast loading
// Temporary local phone codes (most common countries)
const globalPhoneCodes = [
  { value: "+91", label: "ðŸ‡®ðŸ‡³ +91", country: "India", flag: "ðŸ‡®ðŸ‡³" },
  { value: "+1", label: "ðŸ‡ºðŸ‡¸ +1", country: "United States", flag: "ðŸ‡ºðŸ‡¸" },
  { value: "+86", label: "ðŸ‡¨ðŸ‡³ +86", country: "China", flag: "ðŸ‡¨ðŸ‡³" },
  { value: "+81", label: "ðŸ‡¯ðŸ‡µ +81", country: "Japan", flag: "ðŸ‡¯ðŸ‡µ" },
  { value: "+49", label: "ðŸ‡©ðŸ‡ª +49", country: "Germany", flag: "ðŸ‡©ðŸ‡ª" },
  { value: "+33", label: "ðŸ‡«ðŸ‡· +33", country: "France", flag: "ðŸ‡«ðŸ‡·" },
  { value: "+44", label: "ðŸ‡¬ðŸ‡§ +44", country: "United Kingdom", flag: "ðŸ‡¬ðŸ‡§" },
  { value: "+82", label: "ðŸ‡°ðŸ‡· +82", country: "South Korea", flag: "ðŸ‡°ðŸ‡·" },
  { value: "+61", label: "ðŸ‡¦ðŸ‡º +61", country: "Australia", flag: "ðŸ‡¦ðŸ‡º" },
  { value: "+7", label: "ðŸ‡·ðŸ‡º +7", country: "Russia", flag: "ðŸ‡·ðŸ‡º" },
  { value: "+971", label: "ðŸ‡¦ðŸ‡ª +971", country: "UAE", flag: "ðŸ‡¦ðŸ‡ª" },
  { value: "+65", label: "ðŸ‡¸ðŸ‡¬ +65", country: "Singapore", flag: "ðŸ‡¸ðŸ‡¬" },
  { value: "+52", label: "ðŸ‡²ðŸ‡½ +52", country: "Mexico", flag: "ðŸ‡²ðŸ‡½" },
  { value: "+55", label: "ðŸ‡§ðŸ‡· +55", country: "Brazil", flag: "ðŸ‡§ðŸ‡·" },
  { value: "+39", label: "ðŸ‡®ðŸ‡¹ +39", country: "Italy", flag: "ðŸ‡®ðŸ‡¹" },
  { value: "+34", label: "ðŸ‡ªðŸ‡¸ +34", country: "Spain", flag: "ðŸ‡ªðŸ‡¸" },
  { value: "+31", label: "ðŸ‡³ðŸ‡± +31", country: "Netherlands", flag: "ðŸ‡³ðŸ‡±" },
  { value: "+41", label: "ðŸ‡¨ðŸ‡­ +41", country: "Switzerland", flag: "ðŸ‡¨ðŸ‡­" },
  { value: "+46", label: "ðŸ‡¸ðŸ‡ª +46", country: "Sweden", flag: "ðŸ‡¸ðŸ‡ª" },
  { value: "+47", label: "ðŸ‡³ðŸ‡´ +47", country: "Norway", flag: "ðŸ‡³ðŸ‡´" },
];

export default function Login() {
  const [, setLocation] = useLocation();
  const [showPassword, setShowPassword] = useState(false);
  const [rememberMe, setRememberMe] = useState(false);
  const [loginMethod, setLoginMethod] = useState("mobile_otp"); // mobile_otp, userid_password, mobile_password
  const [otpSent, setOtpSent] = useState(false);
  const [formData, setFormData] = useState({
    // Mobile + OTP
    countryCode: "+91",
    phoneNumber: "",
    otp: "",

    // User ID + Password
    userId: "",

    // Mobile + Password  
    password: "",
  });
  const { toast } = useToast();

  // Send OTP Mutation for Mobile + OTP login
  const sendLoginOtpMutation = useMutation({
    mutationFn: async (phoneData: { countryCode: string; phoneNumber: string }) => {
      const response = await apiRequest("POST", "/api/auth/send-login-otp", { 
        ...phoneData, 
        purpose: "login" 
      });
      return response.json();
    },
    onSuccess: () => {
      setOtpSent(true);
      toast({
        title: "OTP Sent",
        description: "Please check your phone for the login verification code",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Failed to Send OTP",
        description: error.message || "Unable to send verification code. Please try again.",
        variant: "destructive",
      });
    },
  });

  // Main Login Mutation for all three methods
  const loginMutation = useMutation({
    mutationFn: async (loginData: any) => {
      let endpoint = "";
      let payload = {};

      switch (loginMethod) {
        case "mobile_otp":
          endpoint = "/api/auth/login-mobile-otp";
          payload = {
            countryCode: loginData.countryCode,
            phoneNumber: loginData.phoneNumber,
            otp: loginData.otp,
          };
          break;
        case "userid_password":
          endpoint = "/api/auth/login-userid-password";
          payload = {
            userId: loginData.userId,
            password: loginData.password,
          };
          break;
        case "mobile_password":
          endpoint = "/api/auth/login-mobile-password";
          payload = {
            countryCode: loginData.countryCode,
            phoneNumber: loginData.phoneNumber,
            password: loginData.password,
          };
          break;
      }

      const response = await apiRequest("POST", endpoint, payload);
      return response.json();
    },
    onSuccess: (data) => {
      localStorage.setItem("token", data.token);
      toast({
        title: "Login Successful",
        description: `Welcome back, ${data.user.fullName || data.user.username}!`,
      });

      // Redirect based on user role
      if (data.user.isAdmin) {
        setLocation("/admin/dashboard");
      } else {
        setLocation("/");
      }
    },
    onError: (error: any) => {
      toast({
        title: "Login Failed",
        description: error.message || "Invalid credentials. Please try again.",
        variant: "destructive",
      });
    },
  });

  const sendOtp = () => {
    if (!formData.phoneNumber) {
      toast({
        title: "Phone Number Required",
        description: "Please enter your phone number first",
        variant: "destructive",
      });
      return;
    }
    sendLoginOtpMutation.mutate({
      countryCode: formData.countryCode,
      phoneNumber: formData.phoneNumber,
    });
  };

  const validateForm = () => {
    switch (loginMethod) {
      case "mobile_otp":
        if (!formData.phoneNumber || !formData.otp) {
          toast({
            title: "Validation Error",
            description: "Please enter your phone number and OTP",
            variant: "destructive",
          });
          return false;
        }
        break;
      case "userid_password":
        if (!formData.userId || !formData.password) {
          toast({
            title: "Validation Error",
            description: "Please enter your User ID and password",
            variant: "destructive",
          });
          return false;
        }
        break;
      case "mobile_password":
        if (!formData.phoneNumber || !formData.password) {
          toast({
            title: "Validation Error",
            description: "Please enter your phone number and password",
            variant: "destructive",
          });
          return false;
        }
        break;
    }
    return true;
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) return;

    loginMutation.mutate(formData);
  };

  return (
    <div className="min-h-screen pt-16 pb-16 flex items-center justify-center" data-testid="login-page">
      <div className="max-w-md w-full mx-4">
        <GlassCard className="p-8">
          {/* Header */}
          <div className="text-center mb-8">
            <div className="w-16 h-16 rounded-lg bg-primary flex items-center justify-center neon-border mx-auto mb-4">
              <span className="text-primary-foreground font-bold text-2xl">JG</span>
            </div>
            <h1 className="text-3xl font-bold neon-text text-primary mb-2" data-testid="login-title">
              Welcome Back
            </h1>
            <p className="text-muted-foreground">
              Choose your preferred login method to access your cosmic journey
            </p>
          </div>

          {/* Login Method Selection */}
          <div className="mb-6">
            <Label className="text-foreground mb-3 block">Choose Login Method</Label>
            <div className="grid grid-cols-1 gap-3">
              <Button
                type="button"
                variant={loginMethod === "mobile_otp" ? "default" : "outline"}
                onClick={() => {
                  setLoginMethod("mobile_otp");
                  setOtpSent(false);
                  setFormData({ ...formData, otp: "" });
                }}
                className={`w-full text-left justify-start space-x-2 ${
                  loginMethod === "mobile_otp" ? "neon-border bg-primary" : "glass"
                }`}
                data-testid="mobile-otp-method"
              >
                <Smartphone className="w-4 h-4" />
                <div>
                  <div className="font-medium">Mobile + OTP</div>
                  <div className="text-xs opacity-70">Most secure option</div>
                </div>
              </Button>

              <Button
                type="button"
                variant={loginMethod === "userid_password" ? "default" : "outline"}
                onClick={() => setLoginMethod("userid_password")}
