                            control={form.control}
                            name="studentPhone"
                            render={({ field }) => (
                              <FormItem>
                                <FormLabel className="text-gray-300">
                                  <Phone className="w-4 h-4 inline mr-1" />
                                  Phone Number
                                </FormLabel>
                                <FormControl>
                                  <Input 
                                    {...field} 
                                    className="bg-white/10 border-white/20 text-white"
                                    placeholder="10-digit phone number"
                                  />
                                </FormControl>
                                <FormMessage />
                              </FormItem>
                            )}
                          />

                          <FormField
                            control={form.control}
                            name="studentEmail"
                            render={({ field }) => (
                              <FormItem>
                                <FormLabel className="text-gray-300">
                                  <Mail className="w-4 h-4 inline mr-1" />
                                  Email (Optional)
                                </FormLabel>
                                <FormControl>
                                  <Input 
                                    {...field} 
                                    type="email"
