            <p className="text-sm text-foreground">
              <strong>Note:</strong> All notification services work in development mode with console logging. 
              Once you configure the API credentials, real SMS and emails will be sent.
            </p>
          </div>
        </div>
      </GlassCard>
    </div>
  );
}

client/src/pages/checkout.tsx.content:
import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { useQuery, useMutation } from "@tanstack/react-query";
import { GlassCard } from "@/components/ui/glass-card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Label } from "@/components/ui/label";
import { 
  CreditCard, 
  Smartphone, 
  QrCode, 
  Building, 
  ShieldCheck,
  ArrowLeft,
  Lock,
  CheckCircle,
  Info,
  Loader2
} from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";


interface CheckoutItem {
  id: string;
  type: 'consultation' | 'course' | 'product';
  name: string;
  description?: string;
  price: number;
  duration?: number;
  quantity?: number;
}

export default function Checkout() {
  const [, setLocation] = useLocation();
  const [paymentMethod, setPaymentMethod] = useState("upi_intent");
  const [upiId, setUpiId] = useState("");
  const [isProcessing, setIsProcessing] = useState(false);
  const { toast } = useToast();

  // Get cart items from localStorage or URL params
  const [cartItems, setCartItems] = useState<CheckoutItem[]>([]);

  useEffect(() => {
    const storedCart = localStorage.getItem("cart");
    if (storedCart) {
      setCartItems(JSON.parse(storedCart));
    } else {
      // If no cart, redirect to home
      setLocation("/");
    }
  }, [setLocation]);

  const { data: user } = useQuery({
    queryKey: ["/api/profile"],
    enabled: !!localStorage.getItem("token"),
  });

  const createPaymentMutation = useMutation({
    mutationFn: async (paymentData: any) => {
      return apiRequest("POST", "/api/payments/phonepe/create", paymentData);
    },
    onSuccess: (data) => {
      if (data.success && data.paymentUrl) {
        // Redirect to PhonePe payment page
        window.location.href = data.paymentUrl;
      } else {
        toast({
          title: "Payment Error",
          description: data.message || "Failed to initiate payment",
          variant: "destructive",
        });
      }
    },
    onError: (error: any) => {
      toast({
        title: "Payment Error",
        description: error.message || "Failed to create payment",
        variant: "destructive",
      });
      setIsProcessing(false);
    },
  });

  const totalAmount = cartItems.reduce((sum, item) => sum + (item.price * (item.quantity || 1)), 0);
  const gst = Math.round(totalAmount * 0.18);
  const finalAmount = totalAmount + gst;

  const handlePayment = async () => {
    if (!user) {
      toast({
        title: "Authentication Required",
        description: "Please login to continue with payment",
        variant: "destructive",
      });
      setLocation("/login");
      return;
    }

    if (paymentMethod === "upi_collect" && !upiId) {
      toast({
        title: "UPI ID Required",
        description: "Please enter your UPI ID for UPI Collect payment",
        variant: "destructive",
      });
      return;
    }

    setIsProcessing(true);

    const paymentData = {
      amount: finalAmount * 100, // Convert to paisa
      merchantOrderId: `ORDER_${Date.now()}_${user.id}`,
      merchantUserId: user.id,
      items: cartItems,
      paymentMethod,
      upiId: paymentMethod === "upi_collect" ? upiId : undefined,
      redirectUrl: `${window.location.origin}/payment/success`,
      callbackUrl: `${window.location.origin}/api/payments/phonepe/callback`,
    };

    createPaymentMutation.mutate(paymentData);
  };

  const paymentMethods = [
    {
      id: "upi_intent",
      name: "UPI Apps",
      description: "Pay using PhonePe, Google Pay, Paytm, etc.",
      icon: Smartphone,
      popular: true
    },
    {
      id: "upi_collect",
      name: "UPI ID",
      description: "Enter your UPI ID to receive payment request",
      icon: QrCode,
      popular: false
    },
    {
      id: "upi_qr", 
      name: "UPI QR Code",
      description: "Scan QR code with any UPI app",
      icon: QrCode,
      popular: false
    },
    {
      id: "card",
      name: "Debit/Credit Card",
      description: "Visa, Mastercard, RuPay cards accepted",
      icon: CreditCard,
      popular: false
    },
    {
      id: "netbanking",
      name: "Net Banking",
      description: "Pay directly from your bank account",
      icon: Building,
      popular: false
    }
  ];

  if (!user) {
    return (
      <div className="min-h-screen pt-16 flex items-center justify-center">
        <GlassCard className="p-8 text-center max-w-md">
          <h2 className="text-2xl font-bold text-foreground mb-4">Login Required</h2>
          <p className="text-muted-foreground mb-6">Please login to proceed with checkout</p>
          <Button onClick={() => setLocation("/login")} className="neon-button">
            Login to Continue
          </Button>
        </GlassCard>
      </div>
    );
  }

  if (cartItems.length === 0) {
    return (
      <div className="min-h-screen pt-16 flex items-center justify-center">
        <GlassCard className="p-8 text-center max-w-md">
          <h2 className="text-2xl font-bold text-foreground mb-4">Empty Cart</h2>
          <p className="text-muted-foreground mb-6">Add items to cart before checkout</p>
          <Button onClick={() => setLocation("/")} className="neon-button">
            Continue Shopping
          </Button>
        </GlassCard>
      </div>
    );
  }

  return (
    <div className="min-h-screen pt-16 pb-16" data-testid="checkout-page">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <div className="flex items-center mb-8">
          <Button
            variant="ghost"
            onClick={() => setLocation(-1)}
            className="mr-4 text-muted-foreground hover:text-foreground"
          >
            <ArrowLeft className="w-4 h-4" />
          </Button>
          <div>
            <h1 className="text-3xl font-bold neon-text text-primary mb-2">
              Secure Checkout
            </h1>
            <div className="flex items-center text-muted-foreground">
              <Lock className="w-4 h-4 mr-2" />
              <span>SSL Encrypted & Secure Payment</span>
            </div>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Order Summary */}
          <div className="lg:col-span-1">
            <GlassCard className="p-6 sticky top-24">
              <h3 className="text-xl font-bold text-foreground mb-4">Order Summary</h3>

              <div className="space-y-4 mb-6">
                {cartItems.map((item, index) => (
                  <div key={index} className="flex justify-between items-start">
                    <div className="flex-1">
                      <p className="font-medium text-foreground">{item.name}</p>
                      {item.description && (
                        <p className="text-sm text-muted-foreground">{item.description}</p>
                      )}
                      {item.duration && (
                        <p className="text-sm text-primary">{item.duration} minutes</p>
                      )}
                      {item.quantity && item.quantity > 1 && (
                        <p className="text-sm text-muted-foreground">Qty: {item.quantity}</p>
                      )}
                    </div>
                    <div className="text-right">
                      <p className="font-semibold text-foreground">
                        â‚¹{(item.price * (item.quantity || 1)).toLocaleString()}
                      </p>
                    </div>
                  </div>
                ))}
              </div>

              <div className="border-t border-primary/20 pt-4 space-y-2">
                <div className="flex justify-between text-muted-foreground">
                  <span>Subtotal</span>
                  <span>â‚¹{totalAmount.toLocaleString()}</span>
                </div>
                <div className="flex justify-between text-muted-foreground">
                  <span>GST (18%)</span>
                  <span>â‚¹{gst.toLocaleString()}</span>
                </div>
                <div className="flex justify-between text-lg font-bold text-foreground pt-2 border-t border-primary/20">
                  <span>Total</span>
                  <span>â‚¹{finalAmount.toLocaleString()}</span>
                </div>
              </div>
            </GlassCard>
          </div>

          {/* Payment Methods */}
          <div className="lg:col-span-2">
            <GlassCard className="p-6">
              <h3 className="text-xl font-bold text-foreground mb-6">Choose Payment Method</h3>

              <RadioGroup value={paymentMethod} onValueChange={setPaymentMethod} className="space-y-4">
                {paymentMethods.map((method) => (
                  <div key={method.id} className="relative">
                    <div className={`glass p-4 rounded-lg cursor-pointer transition-all ${
                      paymentMethod === method.id ? 'ring-2 ring-primary' : 'hover:bg-primary/5'
                    }`}>
                      <div className="flex items-center space-x-4">
                        <RadioGroupItem value={method.id} id={method.id} />
                        <div className="flex items-center space-x-3 flex-1">
                          <div className="w-12 h-12 bg-primary/20 rounded-lg flex items-center justify-center">
                            <method.icon className="w-6 h-6 text-primary" />
                          </div>
                          <div className="flex-1">
                            <div className="flex items-center space-x-2">
                              <Label htmlFor={method.id} className="font-medium text-foreground cursor-pointer">
                                {method.name}
                              </Label>
                              {method.popular && (
                                <Badge variant="secondary" className="text-xs">Most Popular</Badge>
                              )}
                            </div>
                            <p className="text-sm text-muted-foreground">{method.description}</p>
                          </div>
                        </div>
                      </div>
                    </div>

                    {/* UPI Collect Input */}
                    {paymentMethod === "upi_collect" && method.id === "upi_collect" && (
                      <div className="mt-4 p-4 bg-background/50 rounded-lg">
                        <Label htmlFor="upiId" className="text-sm font-medium text-foreground">
                          Enter UPI ID
                        </Label>
                        <Input
                          id="upiId"
                          type="text"
                          placeholder="yourname@paytm"
                          value={upiId}
                          onChange={(e) => setUpiId(e.target.value)}
                          className="mt-2 form-input"
                        />
                      </div>
                    )}
                  </div>
                ))}
              </RadioGroup>

              {/* Security Notice */}
              <div className="mt-6 p-4 bg-green-500/10 border border-green-500/20 rounded-lg">
                <div className="flex items-start space-x-3">
                  <ShieldCheck className="w-5 h-5 text-green-400 mt-0.5" />
                  <div>
                    <p className="text-sm font-medium text-green-400">100% Secure Payment</p>
                    <p className="text-xs text-muted-foreground mt-1">
                      Your payment is protected by 256-bit SSL encryption and processed through PhonePe's secure gateway.
                    </p>
                  </div>
                </div>
              </div>

              {/* Pay Button */}
              <Button
                onClick={handlePayment}
                disabled={isProcessing}
                className="w-full mt-6 neon-button text-lg py-6"
                data-testid="pay-button"
              >
                {isProcessing ? (
                  <>
                    <Loader2 className="w-5 h-5 mr-2 animate-spin" />
                    Processing Payment...
                  </>
                ) : (
                  <>
                    <Lock className="w-5 h-5 mr-2" />
                    Pay â‚¹{finalAmount.toLocaleString()}
                  </>
                )}
              </Button>

              {/* Terms */}
              <p className="text-xs text-muted-foreground text-center mt-4">
                By proceeding with payment, you agree to our{" "}
                <span className="text-primary cursor-pointer">Terms & Conditions</span> and{" "}
                <span className="text-primary cursor-pointer">Privacy Policy</span>
              </p>
            </GlassCard>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/pages/mobile-login.tsx.content:
import { useState } from "react";
import { useLocation } from "wouter";
import { useMutation } from "@tanstack/react-query";
import { GlassCard } from "@/components/ui/glass-card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { 
  Smartphone, 
  MessageSquare, 
  Shield, 
  ArrowLeft,
  CheckCircle,
  Loader2,
  Timer,
  User,
  Calendar,
  MapPin
} from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";


export default function MobileLogin() {
  const [, setLocation] = useLocation();
  const [step, setStep] = useState<'phone' | 'otp' | 'details'>('phone');
  const [selectedTab, setSelectedTab] = useState('login');
  const [phoneNumber, setPhoneNumber] = useState("");
  const [countryCode, setCountryCode] = useState("+91");
  const [otp, setOtp] = useState("");
  const [otpTimer, setOtpTimer] = useState(0);
  const [userDetails, setUserDetails] = useState({
    fullName: "",
    email: "",
    dateOfBirth: "",
    placeOfBirth: "",
    timeOfBirth: "",
    username: ""
  });
  const { toast } = useToast();

  const sendOtpMutation = useMutation({
    mutationFn: async ({ phone, code, purpose }: { phone: string; code: string; purpose: string }) => {
      return apiRequest("POST", "/api/auth/send-otp", {
        phoneNumber: phone,
        countryCode: code,
        purpose
      });
    },
    onSuccess: (data) => {
      setStep('otp');
      setOtpTimer(600); // 10 minutes
      startTimer();
      toast({
        title: "OTP Sent",
        description: `Verification code sent to ${countryCode} ${phoneNumber}`,
      });
      // Show OTP in development
      if (data.otp) {
        toast({
          title: "Development Mode",
          description: `Your OTP is: ${data.otp}`,
          duration: 10000,
        });
      }
    },
    onError: (error: any) => {
      toast({
        title: "Failed to Send OTP",
        description: error.message || "Please try again",
        variant: "destructive",
      });
    },
  });

  const verifyOtpMutation = useMutation({
    mutationFn: async (data: any) => {
      return apiRequest("POST", "/api/auth/verify-otp", data);
    },
    onSuccess: (data) => {
      localStorage.setItem("token", data.token);
      toast({
        title: "Success!",
        description: "You have been successfully logged in",
      });
      setLocation("/");
    },
    onError: (error: any) => {
      toast({
        title: "Verification Failed",
        description: error.message || "Invalid OTP. Please try again.",
        variant: "destructive",
      });
    },
  });

  const startTimer = () => {
    const interval = setInterval(() => {
      setOtpTimer((prev) => {
        if (prev <= 1) {
          clearInterval(interval);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
  };

  const formatTimer = (seconds: number) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
  };

  const handleSendOtp = () => {
    if (!phoneNumber || phoneNumber.length < 10) {
      toast({
        title: "Invalid Phone Number",
        description: "Please enter a valid 10-digit mobile number",
        variant: "destructive",
      });
      return;
    }

    sendOtpMutation.mutate({
      phone: phoneNumber,
      code: countryCode,
      purpose: selectedTab
    });
  };

  const handleVerifyOtp = () => {
    if (!otp || otp.length !== 6) {
      toast({
        title: "Invalid OTP",
        description: "Please enter the complete 6-digit OTP",
        variant: "destructive",
      });
      return;
    }

    if (selectedTab === 'registration') {
      setStep('details');
    } else {
      verifyOtpMutation.mutate({
        phoneNumber,
        countryCode,
        otp,
        purpose: selectedTab
      });
    }
  };

  const handleCompleteRegistration = () => {
    if (!userDetails.fullName || !userDetails.email || !userDetails.username) {
      toast({
        title: "Missing Information",
        description: "Please fill in all required fields",
        variant: "destructive",
      });
      return;
    }

    verifyOtpMutation.mutate({
      phoneNumber,
      countryCode,
      otp,
      purpose: 'registration',
      userData: userDetails
    });
  };

  const countries = [
    { code: "+91", name: "India", flag: "ðŸ‡®ðŸ‡³" },
    { code: "+1", name: "USA", flag: "ðŸ‡ºðŸ‡¸" },
    { code: "+44", name: "UK", flag: "ðŸ‡¬ðŸ‡§" },
    { code: "+971", name: "UAE", flag: "ðŸ‡¦ðŸ‡ª" },
    { code: "+65", name: "Singapore", flag: "ðŸ‡¸ðŸ‡¬" },
    { code: "+61", name: "Australia", flag: "ðŸ‡¦ðŸ‡º" },
    { code: "+49", name: "Germany", flag: "ðŸ‡©ðŸ‡ª" },
  ];

  return (
    <div className="min-h-screen pt-16 pb-16" data-testid="mobile-login">
      <div className="max-w-md mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <div className="text-center mb-8">
          <Button
            variant="ghost"
            onClick={() => step === 'phone' ? setLocation("/") : setStep('phone')}
            className="absolute left-4 top-20 text-muted-foreground hover:text-foreground"
          >
            <ArrowLeft className="w-4 h-4" />
          </Button>

          <div className="w-16 h-16 bg-gradient-to-br from-primary to-secondary rounded-full flex items-center justify-center mx-auto mb-4">
            <Smartphone className="w-8 h-8 text-white" />
          </div>
          <h1 className="text-2xl font-bold neon-text text-primary mb-2">
            "Mobile Verification"
          </h1>
          <p className="text-muted-foreground">
            "Secure login with OTP verification"
          </p>
        </div>

        <GlassCard className="p-6">
          {step === 'phone' && (
            <>
              <Tabs value={selectedTab} onValueChange={setSelectedTab} className="mb-6">
                <TabsList className="grid w-full grid-cols-2 glass-card">
                  <TabsTrigger value="login">"Login"</TabsTrigger>
                  <TabsTrigger value="registration">"Sign Up"</TabsTrigger>
                </TabsList>
              </Tabs>

              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-foreground mb-2">
                    "Country"
                  </label>
                  <Select value={countryCode} onValueChange={setCountryCode}>
                    <SelectTrigger className="form-input">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent className="glass-card">
                      {countries.map((country) => (
                        <SelectItem key={country.code} value={country.code}>
                          <div className="flex items-center space-x-2">
                            <span>{country.flag}</span>
                            <span>{country.name}</span>
                            <span className="text-muted-foreground">{country.code}</span>
                          </div>
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-foreground mb-2">
                    Mobile Number
                  </label>
                  <div className="flex">
                    <div className="px-3 py-2 bg-background/50 border border-primary/20 rounded-l-lg text-muted-foreground">
                      {countryCode}
                    </div>
                    <Input
                      type="tel"
                      placeholder="9876543210"
                      value={phoneNumber}
                      onChange={(e) => setPhoneNumber(e.target.value)}
                      className="rounded-l-none form-input"
                      maxLength={10}
                      data-testid="phone-input"
                    />
                  </div>
                </div>

                <Button
                  onClick={handleSendOtp}
                  disabled={sendOtpMutation.isPending}
                  className="w-full neon-button"
                  data-testid="send-otp-button"
                >
                  {sendOtpMutation.isPending ? (
                    <>
                      <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                      Sending OTP...
                    </>
                  ) : (
                    <>
                      <MessageSquare className="w-4 h-4 mr-2" />
                      Send OTP
                    </>
                  )}
                </Button>
              </div>
            </>
          )}

          {step === 'otp' && (
            <div className="space-y-4">
              <div className="text-center mb-6">
                <div className="w-16 h-16 bg-green-500/20 rounded-full flex items-center justify-center mx-auto mb-3">
                  <MessageSquare className="w-8 h-8 text-green-400" />
                </div>
                <h3 className="text-lg font-semibold text-foreground mb-2">
                  Enter Verification Code
                </h3>
                <p className="text-sm text-muted-foreground">
                  We've sent a 6-digit code to<br />
                  <strong>{countryCode} {phoneNumber}</strong>
                </p>
              </div>

              <div>
                <label className="block text-sm font-medium text-foreground mb-2">
                  6-Digit OTP
                </label>
                <Input
                  type="text"
                  placeholder="123456"
                  value={otp}
                  onChange={(e) => setOtp(e.target.value)}
                  className="form-input text-center text-2xl tracking-widest"
                  maxLength={6}
                  data-testid="otp-input"
                />
              </div>

              {otpTimer > 0 && (
                <div className="flex items-center justify-center text-muted-foreground">
                  <Timer className="w-4 h-4 mr-2" />
                  <span className="text-sm">Resend OTP in {formatTimer(otpTimer)}</span>
                </div>
              )}

              <Button
                onClick={handleVerifyOtp}
                disabled={verifyOtpMutation.isPending}
                className="w-full neon-button"
                data-testid="verify-otp-button"
              >
                {verifyOtpMutation.isPending ? (
                  <>
                    <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                    Verifying...
                  </>
                ) : (
                  <>
                    <CheckCircle className="w-4 h-4 mr-2" />
                    Verify OTP
                  </>
                )}
              </Button>

              {otpTimer === 0 && (
                <Button
                  variant="outline"
                  onClick={handleSendOtp}
                  disabled={sendOtpMutation.isPending}
                  className="w-full glass"
                >
                  Resend OTP
                </Button>
              )}
            </div>
          )}

          {step === 'details' && (
            <div className="space-y-4">
              <div className="text-center mb-6">
                <div className="w-16 h-16 bg-primary/20 rounded-full flex items-center justify-center mx-auto mb-3">
                  <User className="w-8 h-8 text-primary" />
                </div>
                <h3 className="text-lg font-semibold text-foreground mb-2">
                  Complete Your Profile
                </h3>
                <p className="text-sm text-muted-foreground">
                  Help us serve you better with accurate astrological guidance
                </p>
              </div>

              <div>
                <label className="block text-sm font-medium text-foreground mb-2">
                  Full Name *
                </label>
                <Input
                  placeholder={"Your full name"}
                  value={userDetails.fullName}
                  onChange={(e) => setUserDetails({...userDetails, fullName: e.target.value})}
                  className="form-input"
                  data-testid="fullname-input"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-foreground mb-2">
                  Username *
                </label>
                <Input
                  placeholder={"Choose a unique username"}
                  value={userDetails.username}
                  onChange={(e) => setUserDetails({...userDetails, username: e.target.value})}
                  className="form-input"
                  data-testid="username-input"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-foreground mb-2">
                  Email *
                </label>
                <Input
                  type="email"
                  placeholder="your.email@example.com"
                  value={userDetails.email}
                  onChange={(e) => setUserDetails({...userDetails, email: e.target.value})}
                  className="form-input"
                  data-testid="email-input"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-foreground mb-2">
                  Date of Birth
                </label>
                <Input
                  type="date"
                  value={userDetails.dateOfBirth}
                  onChange={(e) => setUserDetails({...userDetails, dateOfBirth: e.target.value})}
                  className="form-input"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-foreground mb-2">
                  Time of Birth
                </label>
                <Input
                  type="time"
                  value={userDetails.timeOfBirth}
                  onChange={(e) => setUserDetails({...userDetails, timeOfBirth: e.target.value})}
                  className="form-input"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-foreground mb-2">
                  Place of Birth
                </label>
                <Input
                  placeholder="City, State, Country"
                  value={userDetails.placeOfBirth}
                  onChange={(e) => setUserDetails({...userDetails, placeOfBirth: e.target.value})}
                  className="form-input"
                />
              </div>

              <Button
                onClick={handleCompleteRegistration}
                disabled={verifyOtpMutation.isPending}
                className="w-full neon-button"
                data-testid="complete-registration-button"
              >
                {verifyOtpMutation.isPending ? (
                  <>
                    <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                    Creating Account...
                  </>
                ) : (
                  <>
                    <CheckCircle className="w-4 h-4 mr-2" />
                    Complete Registration
                  </>
                )}
              </Button>
            </div>
          )}

          {/* Security Notice */}
          <div className="mt-6 p-4 bg-primary/10 border border-primary/20 rounded-lg">
            <div className="flex items-start space-x-3">
              <Shield className="w-5 h-5 text-primary mt-0.5" />
              <div>
                <p className="text-sm font-medium text-primary">Secure & Private</p>
                <p className="text-xs text-muted-foreground mt-1">
                  Your mobile number is encrypted and never shared with third parties.
                  OTP verification ensures the highest security for your account.
                </p>
              </div>
            </div>
          </div>
        </GlassCard>
      </div>
    </div>
  );
}

client/src/pages/payment/success.tsx.content:
import { useEffect, useState } from "react";
import { useLocation } from "wouter";
import { useQuery } from "@tanstack/react-query";
import { GlassCard } from "@/components/ui/glass-card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { 
  CheckCircle, 
  Download, 
  Calendar, 
  ArrowRight, 
  Home,
  Receipt,
  Share2,
  Clock
} from "lucide-react";


export default function PaymentSuccess() {
  const [, setLocation] = useLocation();
  const [transactionId, setTransactionId] = useState<string>("");
  const [orderDetails, setOrderDetails] = useState<any>(null);

  // Get transaction ID from URL params
  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const txnId = urlParams.get('transactionId') || urlParams.get('merchantTransactionId');
    if (txnId) {
      setTransactionId(txnId);
    }

    // Clear cart after successful payment
    localStorage.removeItem('cart');
  }, []);

  const { data: paymentDetails, isLoading } = useQuery({
    queryKey: ["/api/payments/details", transactionId],
    enabled: !!transactionId,
  });

  useEffect(() => {
    if (paymentDetails) {
      setOrderDetails(paymentDetails);
    }
  }, [paymentDetails]);

  if (isLoading) {
    return (
      <div className="min-h-screen pt-16 flex items-center justify-center">
        <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
      </div>
    );
  }

  return (
    <div className="min-h-screen pt-16 pb-16" data-testid="payment-success">
      <div className="max-w-2xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Success Animation */}
        <div className="text-center mb-8">
          <div className="w-24 h-24 bg-green-500 rounded-full flex items-center justify-center mx-auto mb-6 animate-pulse">
            <CheckCircle className="w-12 h-12 text-white" />
          </div>
          <h1 className="text-3xl font-bold neon-text text-primary mb-2">
            "Payment Successful!"
          </h1>
          <p className="text-muted-foreground">
            "Thank you for your payment. Your order has been confirmed."
          </p>
        </div>

        {/* Payment Details */}
        <GlassCard className="p-6 mb-6">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-semibold text-foreground">"Payment Details"</h3>
            <Badge variant="default" className="bg-green-500">
              <CheckCircle className="w-3 h-3 mr-1" />
              "Completed"
            </Badge>
          </div>

          <div className="space-y-4">
            <div className="flex justify-between items-center">
              <span className="text-muted-foreground">Transaction ID</span>
              <span className="font-mono text-sm text-foreground">{transactionId}</span>
            </div>

            <div className="flex justify-between items-center">
